import { createStore } from 'vuex';


const store = createStore({
    state:{
        user_data:[],
        auth:false,
        user_id:null

    },
    mutations:{
        setAuthen(state,data){
            //console.log("called for auth")
            console.log(data)
            state.auth = data
        },
        setUserIdentifier(state,data){
            console.log(data)
            state.user_id=data
        }
    },
    actions:{
        setAuth({commit},auth){
            commit('setAuthen',auth)
        },
        setUserId({commit},passed_id){
            commit('setUserIdentifier',passed_id)
        }
    },
    getters:{
        getAuth: state => state.auth,
        getUserId:state => state.user_id
    }

})

export default store
