<script>
import { makeRequest } from '@/axios';
import Chart from 'chart.js/auto';
import { CountUp } from 'countup.js';
import { mapGetters,mapActions } from 'vuex';

import SidebarMixin from '../../SidebarMixin.js'
export default {

    name:'DashboardVue',
    mixins:[SidebarMixin],
    data(){
        return{
            numpresent:0,
            numontime:0,
            numlate:0

        }
    },
    computed:{
        ...mapGetters(['getUser','getAuth'])

    },
    methods:{
        ...mapActions(['setUser','setAuth']),
        async count_today_animate(){
            try{
                    const num= await makeRequest.get('/records/countToday');
                    const options = {
                    duration: 3, 
                    separator: ',',
                    };
                    const numpresent = new CountUp('numpresent',num.data, this.numpresent,options); 
                    numpresent.start(); 
                    //this.numpresent=num.data
                }
                catch(error){
                        console.log(error)

                }

        },
        async on_time_animate(){
            try{
                    const num= await makeRequest.get('/records/countTodayOnTime');
                    const options = {
                    duration: 3, 
                    separator: ',',
                    };
                    const numOnTimeCount = new CountUp('numontime',num.data, this.numontime,options); 
                    numOnTimeCount.start(); 
                    this.numontime=num.data
                }
                catch(error){
                        console.log(error)

                }

        },
        async late_animate(){
            try{
                    const num= await makeRequest.get('/records/countTodayLate');
                    const options = {
                    duration: 3, 
                    separator: ',',
                    };
                    const numLateCount = new CountUp('numlate',num.data, this.numlate,options); 
                    numLateCount.start(); 
                    //this.numlate=num.data
                }
                catch(error){
                        console.log(error)

                }


        },
        async get_present_late(){
            let data=[]
            try{
                    const present= await makeRequest.get('/records/countToday');
                    const onTime = await makeRequest.get('/records/countTodayOnTime');
                    const late = await makeRequest.get('/records/countTodayLate');

                     data = [
                        present.data,onTime.data,late.data
                    ]

                }
                catch(error){
                        console.log(error)

                }
                console.log(data)
                return data;

        },
        async pie_chart(){
            const present_late_ontime = await this.get_present_late()
            const ctx3 = this.$refs.PieChart.getContext('2d');
            const PieChart = new Chart(ctx3, {
                type: "pie",
                data: {
                    labels: ["Present", "Late", "On-time"],
                    datasets: [{
                        backgroundColor: [
                            "rgba(0, 156, 255, .5)",
                            "rgba(0, 156, 255, .4)",
                            "rgba(0, 156, 255, .3)"
                        ],
                        data: [present_late_ontime[0],present_late_ontime[2],present_late_ontime[1]]
                    }]
                },
                options: {
                    responsive: true
                }
            });
            console.log(PieChart)
        },
        async dough_nut(){
            const present_late_ontime = await this.get_present_late()

            const ctx4 = this.$refs.DoughnutChart.getContext('2d');
            const DoughnutChart = new Chart(ctx4, {
                type: "doughnut",
                data: {
                    labels: ["present", "on-time", "Late"],
                    datasets: [{
                        backgroundColor: [
                            "rgba(0, 156, 255, .5)",
                            "rgba(0, 156, 255, .4)",
                            "rgba(0, 156, 255, .3)"
                        ],
                        data: [present_late_ontime[0], present_late_ontime[1], present_late_ontime[2]]
                    }]
                },
                options: {
                    responsive: true
                }
            });
            console.log(DoughnutChart)

        }



    },
    mounted(){
            this.count_today_animate()
            this.on_time_animate()
            this.late_animate()
            //bar chart
            const ctx = this.$refs.BarChart.getContext('2d');
            const BarChart=new Chart(ctx, {
            type: "bar",
            data: {
                labels: ["2016", "2017", "2018", "2019", "2020", "2021", "2022"],
                datasets: [{
                        label: "Present",
                        data: [15, 30, 55, 65, 60, 80, 95],
                        backgroundColor: "rgba(0, 156, 255, .7)"
                    },
                    {
                        label: "Late",
                        data: [8, 35, 40, 60, 70, 55, 75],
                        backgroundColor: "rgba(0, 156, 255, .5)"
                    },
                    {
                        label: "On-time",
                        data: [12, 25, 45, 55, 65, 70, 60],
                        backgroundColor: "rgba(0, 156, 255, .3)"
                    }
                ]
                },
            options: {
                    responsive: true
                    }
            });
            console.log(BarChart)

            //Line chart

            const ctx2 = this.$refs.LineChart.getContext('2d');
            const LineChart = new Chart(ctx2, {
            type: "line",
            data: {
                labels: ["2016", "2017", "2018", "2019", "2020", "2021", "2022"],
                datasets: [{
                        label: "On-time",
                        data: [15, 30, 55, 45, 70, 65, 85],
                        backgroundColor: "rgba(0, 156, 255, .5)",
                        fill: true
                    },
                    {
                        label: "Late",
                        data: [99, 135, 170, 130, 190, 180, 270],
                        backgroundColor: "rgba(0, 156, 255, .3)",
                        fill: true
                    }
                ]
                },
            options: {
                    responsive: true
                 }
            });
            console.log(LineChart)

            //pie chart
            this.pie_chart();
             //Doughnut chart
            this.dough_nut()

            //count ups

            const options = {
            duration: 3, 
            separator: ',',
            };
            const numPresntCountUp = new CountUp('numpresent', this.numpresent,options); 
            numPresntCountUp.start(); 

            const numOnTimeCountUp = new CountUp('numontime',this.numontime,options); 
            numOnTimeCountUp.start(); 

            const numLateCountUp = new CountUp('numlate',this.numlate,options); 
            numLateCountUp.start(); 

    }


}
</script>

