import { createStore } from 'vuex';


const store = createStore({
    state:{
        user_data:[],
        auth:false,
        user_id:null

    },
    mutations:{
        setUserdata(state,data){
            state.user_data = data
        },
        setAuthen(state,data){
            console.log("called for auth")
            state.auth = data
        },
        setUserIdentifier(state,data){
            console.log("called for id")
            state.user_id=data
        }
    },
    actions:{
        setUser({commit},userData){
            commit('setUserdata',userData)
        },
        setAuth({commit},auth){
            commit('setAuthen',auth)
        },
        setUserId({commit},passed_id){
            commit('setUserIdentifier',passed_id)
        }
    },
    getters:{
        getUser: state => state.user_data,
        getAuth: state => state.auth,
        getUserId:state => state.user_id
    }

})

export default store
