/* eslint-disable */
<template>
    <!-- Sale & Revenue Start -->
    <div class="container-fluid pt-4 px-4">
                <div class="row g-4">
                    <div class="col-sm-6 col-xl-4">
                        <div class="bg-light rounded d-flex align-items-center justify-content-between p-4">
                            <i class="fa fa-chart-bar fa-3x text-primary"></i>
                            <div class="ms-3">
                                <h4 class="mb-2">Present Today</h4>
                                <h3 class="mb-0 text-primary" id="numpresent">12</h3>
                            </div>
                        </div>
                    </div>
                    <div class="col-sm-6 col-xl-4">
                        <div class="bg-light rounded d-flex align-items-center justify-content-between p-4">
                            <i class="fa fa-check fa-3x text-primary"></i>
                            <div class="ms-3">
                                <h4 class="mb-2">On-time Today</h4>
                                <h3 class="mb-0 text-primary" id="numontime">12</h3>
                            </div>
                        </div>
                    </div>
                    <div class="col-sm-6 col-xl-4">
                        <div class="bg-light rounded d-flex align-items-center justify-content-between p-4">
                            <i class="fa fa-exclamation-triangle fa-3x text-danger"></i>
                            <div class="ms-3">
                                <h4 class="mb-2">Late Today</h4>
                                <h3 class="mb-0 text-primary" id="numlate">12</h3>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <!-- Sale & Revenue End -->


            <!-- Chart showing presence data -->
            <div class="container-fluid pt-4 px-4">
                <div class="row g-4">
                    <div class="col-sm-12 col-xl-6">
                        <div class="bg-light text-center rounded p-4">
                            <div class="d-flex align-items-center justify-content-between mb-4">
                                <h6 class="mb-0">Bar chart (presence)</h6>
                                <!-- <a href="">Show All</a> -->
                            </div>
                            <canvas ref="BarChart" id="BarChart"></canvas>
                        </div>
                    </div>
                    <div class="col-sm-12 col-xl-6">
                        <div class="bg-light text-center rounded p-4">
                            <div class="d-flex align-items-center justify-content-between mb-4">
                                <h6 class="mb-0">on-time/Late</h6>
                            </div>
                            <canvas ref="LineChart"></canvas>
                        </div>
                    </div>
                </div>
            </div>

            <div class="container-fluid pt-4 px-4">
                <div class="row g-4">
                    <div class="col-sm-12 col-xl-6">
                        <div class="bg-light text-center rounded p-4">
                            <div class="d-flex align-items-center justify-content-between mb-4">
                                <h6 class="mb-0">Pie chart (presence)</h6>
                            </div>
                            <canvas ref="PieChart"></canvas>
                        </div>
                    </div>
                    <div class="col-sm-12 col-xl-6">
                        <div class="bg-light text-center rounded p-4">
                            <div class="d-flex align-items-center justify-content-between mb-4">
                                <h6 class="mb-0">Doughnut Chart(presence)</h6>
                            </div>
                            <canvas ref="DoughnutChart"></canvas>
                        </div>
                    </div>
                </div>
            </div>

            <!--End of charts-->




</template>

<script>
import { makeRequest } from '@/axios';
import Chart from 'chart.js/auto';
import { CountUp } from 'countup.js';
import { mapGetters,mapActions } from 'vuex';

import SidebarMixin from '../../SidebarMixin.js'
export default {

    name:'DashboardVue',
    mixins:[SidebarMixin],
    data(){
        return{
            numpresent: 0,
            numontime:145,
            numlate:65

        }
    },
    computed:{
        ...mapGetters(['getUser','getAuth'])

    },
    methods:{
        ...mapActions(['setUser','setAuth']),
        async count_today_animate(){
            try{
                    const num= await makeRequest.get('/records/countToday');
                    const options = {
                    duration: 3, 
                    separator: ',',
                    };
                    const numpresent = new CountUp('numpresent',num.data, this.numpresent,options); 
                    numpresent.start(); 
                    this.numpresent=num.data
                }
                catch(error){
                        console.log(error)

                }

        },
        on_time_animate(){

        },
        late_animate(){

        }



    },
    mounted(){
            this.count_today_animate()
            this.on_time_animate()
            this.late_animate()
            //bar chart
            const ctx = this.$refs.BarChart.getContext('2d');
            const BarChart=new Chart(ctx, {
            type: "bar",
            data: {
                labels: ["2016", "2017", "2018", "2019", "2020", "2021", "2022"],
                datasets: [{
                        label: "Present",
                        data: [15, 30, 55, 65, 60, 80, 95],
                        backgroundColor: "rgba(0, 156, 255, .7)"
                    },
                    {
                        label: "Late",
                        data: [8, 35, 40, 60, 70, 55, 75],
                        backgroundColor: "rgba(0, 156, 255, .5)"
                    },
                    {
                        label: "On-time",
                        data: [12, 25, 45, 55, 65, 70, 60],
                        backgroundColor: "rgba(0, 156, 255, .3)"
                    }
                ]
                },
            options: {
                    responsive: true
                    }
            });
            console.log(BarChart)

            //Line chart

            const ctx2 = this.$refs.LineChart.getContext('2d');
            const LineChart = new Chart(ctx2, {
            type: "line",
            data: {
                labels: ["2016", "2017", "2018", "2019", "2020", "2021", "2022"],
                datasets: [{
                        label: "On-time",
                        data: [15, 30, 55, 45, 70, 65, 85],
                        backgroundColor: "rgba(0, 156, 255, .5)",
                        fill: true
                    },
                    {
                        label: "Late",
                        data: [99, 135, 170, 130, 190, 180, 270],
                        backgroundColor: "rgba(0, 156, 255, .3)",
                        fill: true
                    }
                ]
                },
            options: {
                    responsive: true
                 }
            });
            console.log(LineChart)

            //pie chart



            const ctx3 = this.$refs.PieChart.getContext('2d');
            const PieChart = new Chart(ctx3, {
                type: "pie",
                data: {
                    labels: ["Present", "Late", "On-time"],
                    datasets: [{
                        backgroundColor: [
                            "rgba(0, 156, 255, .5)",
                            "rgba(0, 156, 255, .4)",
                            "rgba(0, 156, 255, .3)"
                        ],
                        data: [55,24, 15]
                    }]
                },
                options: {
                    responsive: true
                }
            });
            console.log(PieChart)


            //Doughnut chart

            const ctx4 = this.$refs.DoughnutChart.getContext('2d');
            const DoughnutChart = new Chart(ctx4, {
                type: "doughnut",
                data: {
                    labels: ["present", "on-time", "Late"],
                    datasets: [{
                        backgroundColor: [
                            "rgba(0, 156, 255, .5)",
                            "rgba(0, 156, 255, .4)",
                            "rgba(0, 156, 255, .3)"
                        ],
                        data: [55, 24, 15]
                    }]
                },
                options: {
                    responsive: true
                }
            });
            console.log(DoughnutChart)


            //count ups

            const options = {
            duration: 3, 
            separator: ',',
            };
            const numPresntCountUp = new CountUp('numpresent', this.numpresent,options); 
            numPresntCountUp.start(); 

            const numOnTimeCountUp = new CountUp('numontime',this.numontime,options); 
            numOnTimeCountUp.start(); 

            const numLateCountUp = new CountUp('numlate',this.numlate,options); 
            numLateCountUp.start(); 

    }


}
</script>

